
var socket=io.connect();
var na;



$(function(){
	
	$('#login_formid').submit(function(e){
		e.preventDefault();
		na=$('#name').val();
		//console.log(na);
		if($('input[name=newu]:checked').val()==1){
			socket.json.emit('login_new',{
				name: 	$('#name').val(),
				pass: 	$('#pass').val()
			});
		}else{
			socket.json.emit('login_try',{
				name: 	$('#name').val(),
				pass: 	$('#pass').val()
			});
		}	
	

	});

	socket.on('login_pas',function(data){
		if(data==1){
			//na=escape($('#name').val());

			
			console.log(na);
			location.href="http://192.168.3.37:1337/main="+na;
		}else{
			$('#name').val('').focus();
			$('#pass').val('').focus();
		}
	});

});